create table sam_vehicle_speeds (
    id               number generated by default on null as identity not null enable,
    posted_speed     number,
    vehical_speed    number,
    date_of_incedent date,
    license_plate    varchar2(50 byte),
    location         varchar2(50 byte),
    state            varchar2(50 byte)
);

alter table sam_vehicle_speeds add primary key ( id )
    using index enable;


